/**
 * Locks
 */
public class Locks {

    public Object lock1 = new Object();

    public Object lock2 = new Object();

    class ProcessOne implements Runnable {

        /*
         * (non-Javadoc)
         * 
         * @see java.lang.Runnable#run()
         */
        public void run() {
            synchronized (lock1) {
                System.out.println("p1 got lock1");
                try {
                    Thread.sleep(2000);
                } catch (InterruptedException e) {
                }
                synchronized (lock2) {
                    System.out.println("p1 got lock2");
                    lock2.notifyAll();
                    System.out.println("p1 released lock2");
                }
                lock1.notifyAll();
                System.out.println("p1 released lock1");
            }

        }
    }

    class ProcessTwo implements Runnable {

        /*
         * (non-Javadoc)
         * 
         * @see java.lang.Runnable#run()
         */
        public void run() {
            synchronized (lock2) {
                System.out.println("p2 got lock2");
                synchronized (lock1) {
                    System.out.println("p2 got lock1");
                    lock1.notifyAll();
                    System.out.println("p2 released lock1");
                }
                lock2.notifyAll();
                System.out.println("p2 released lock2");
            }

        }
    }

    public static void main(String[] args) {
        new Locks().deadlock();
    }

    public void deadlock() {
        ProcessOne p1 = new ProcessOne();
        ProcessTwo p2 = new ProcessTwo();
        new Thread(p1).start();
        new Thread(p2).start();
    }
}

