/*
 * Tree example snippet: detect mouse down in a tree item
 *
 * For a list of all SWT example snippets see
 * <a href="http://dev.eclipse.org/viewcvs/index.cgi/%7Echeckout%7E/platform-swt">http://dev.eclipse.org/viewcvs/index.cgi/%7Echeckout%7E/platform-swt</a>-
home/dev.html#snippets
 */
import org.eclipse.swt.*;
import org.eclipse.swt.widgets.*;

public static void main (String [] args) {
	Display display = new Display ();
	Shell shell = new Shell (display);
	final Tree tree = new Tree (shell, SWT.BORDER | SWT.MULTI);
	for (int i=0; i&lt;12; i++) {
		TreeItem treeItem = new TreeItem (tree, SWT.NULL);
		treeItem.setText ("Item " + i);
	}
	tree.addListener (SWT.MouseDown, new Listener () {
		public void handleEvent (Event event) {
			Point point = new Point (event.x, event.y);
			TreeItem item = tree.getItem (point);
			if (item != null) {
				System.out.println ("Mouse down: " + item);
			}
		}
	});
	tree.setSize (200, 200);
	shell.setSize (300, 300);
	shell.open ();
	while (!shell.isDisposed ()) {
		if (!display.readAndDispatch ()) display.sleep ();
	}
	display.dispose ();
}

